import React, { useState, useEffect } from 'react';
import { ReactP5Wrapper } from '@p5-wrapper/react';
import './App.css';

function App() {
  // 時計表示用のstate
  const [time, setTime] = useState(new Date());

  useEffect(() => {
    const timer = setInterval(() => {
      setTime(new Date());
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  // p5スケッチ
  const sketch = (p5) => {
    let img;
    let currentHour = (new Date()).getHours();

    p5.preload = () => {
      img = p5.loadImage('/seabeeze.jpg');
    };

    p5.setup = () => {
      p5.createCanvas(p5.windowWidth, p5.windowHeight);
      p5.noLoop();
    };

    p5.draw = () => {
      // 時間帯ごとに色味を変える
      let hour = (new Date()).getHours();
      p5.image(img, 0, 0, p5.width, p5.height);
      p5.loadPixels();
      for (let y = 0; y < p5.height; y++) {
        for (let x = 0; x < p5.width; x++) {
          let idx = 4 * (y * p5.width + x);
          // 朝焼け
          if (hour >= 5 && hour < 8) {
            p5.pixels[idx + 0] = p5.pixels[idx + 0] * 1.1 + 20; // R
            p5.pixels[idx + 1] = p5.pixels[idx + 1] * 0.9 + 10; // G
            p5.pixels[idx + 2] = p5.pixels[idx + 2] * 0.8; // B
          // 昼
          } else if (hour >= 8 && hour < 17) {
            // そのまま
          // 夕焼け
          } else if (hour >= 17 && hour < 20) {
            p5.pixels[idx + 0] = p5.pixels[idx + 0] * 1.2 + 30; // R
            p5.pixels[idx + 1] = p5.pixels[idx + 1] * 0.8 + 10; // G
            p5.pixels[idx + 2] = p5.pixels[idx + 2] * 0.7; // B
          // 夜
          } else {
            p5.pixels[idx + 0] = p5.pixels[idx + 0] * 0.5; // R
            p5.pixels[idx + 1] = p5.pixels[idx + 1] * 0.6; // G
            p5.pixels[idx + 2] = p5.pixels[idx + 2] * 1.2 + 30; // B
          }
        }
      }
      p5.updatePixels();
    };

    p5.windowResized = () => {
      p5.resizeCanvas(p5.windowWidth, p5.windowHeight);
      p5.redraw();
    };
  };

  // 時計表示用フォーマット関数
  const formatTime = (date) => {
    const hours = date.getHours().toString().padStart(2, '0');
    const minutes = date.getMinutes().toString().padStart(2, '0');
    const seconds = date.getSeconds().toString().padStart(2, '0');
    return `${hours}:${minutes}:${seconds}`;
  };

  const formatDate = (date) => {
    const year = date.getFullYear();
    const month = (date.getMonth() + 1).toString().padStart(2, '0');
    const day = date.getDate().toString().padStart(2, '0');
    const weekdays = ['日', '月', '火', '水', '木', '金', '土'];
    const weekday = weekdays[date.getDay()];
    return `${year}年${month}月${day}日 (${weekday})`;
  };

  return (
    <div className="App">
      <ReactP5Wrapper sketch={sketch} />
      <div className="clock-overlay">
        <div className="time">{formatTime(time)}</div>
        <div className="date">{formatDate(time)}</div>
      </div>
    </div>
  );
}

export default App;
